--[[可以在此放入关于该脚本的一些文本说明。--]]-- 请设定脚本名，脚本名以文件名为主。function 基本设定 ()  C.Cycles = 1; -- 脚本执行次数（0 为始终循环）  C.WaitTime = (10) *60*1000 + (0) *1000 + (0); -- 脚本循环等待时间（单位：毫秒）-- (分钟)(秒)(毫秒)  -- 说明：当脚本执行完毕后会判断是否再次执行，如果再次执行会等待 WaitTime 所指示的时间。  点击前延迟时间 = 0; -- 每次点击前将会延迟该时长（单位：毫秒）  出击海域 = 1;  -- 鎮守府海域 = 1; 南西諸島海域 = 2; 北方海域 = 3; 西方海域 = 4; 南方海域 = 5; 中部海域 = 6;  出击地图 = 1;  战斗阵型 = {1,1,1,1,1};-- 1.若没有指示下一战斗阵型则将会撤退（返回母港） 2.若已发生的战斗没有指定阵型则默认使用単縦陣 = 1（联合舰队模式默认使用第四警戒航行序列 = 4）  -- 単縦陣 = 1; 複縦陣 = 2; 輪形陣 = 3; 梯形陣 = 4; 単横陣 = 5;  -- 第一警戒航行序列 = 1; 第二警戒航行序列 = 2; 第三警戒航行序列 = 3; 第四警戒航行序列 = 4;  是否追击 = {1,1,1,1,1,2};-- 若是否追击和战斗阵型不匹配则默认不追击  -- 追撃せず = 1; 夜戦突入 = 2;  检测破损程度下限 = 3;-- 3=仅检测大破、2=检测中破和大破、1=检测小破中破和大破  联合舰队模式 = false;  待补给舰船 = {true,true,true,true,true,true};  是否开启自动入渠 = true;  修复破损程度下限 = 2;-- 3=仅修复大破、2=修复中破和大破、1=修复小破中破和大破end-- ！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！-- ！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！-- ！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！-- 修改下面的代码可能会导致运行错误，请不会lua脚本的小伙伴谨慎使用function 自定义判定接口函数1 ()  -- 该函数位于检测进入地图后的画面中（进入地图之后，选择阵型之前）  if false then -- 开启或关闭自定义接口    -- 可用参数：战斗场次    -- 在此处定义能动分歧目标    -- 在此处定义路基航空队攻击目标    -- 在此定义深海舰船对话判定  endendfunction 自定义海域选择 ()  return false; -- false表示不使用自定义海域选择  -- 在此定义想要进入的海域（活动图）  -- 在此定义补给路基航空队  -- return true; -- true表示使用自定义海域选择end--------------------------------------------------function Judge (judgeData,...) -- 页面判断  -- /// <param name="data">需要判定的数据</param>  -- /// <param name="rin">容许色差阈值（0~441）</param>  -- /// <param name="OffsetX">X坐标偏移</param>  -- /// <param name="OffsetY">Y坐标偏移</param>  -- public virtual bool Judge(DataJudge data, int rin = 0, int OffsetX = 0, int OffsetY = 0);  return C:Judge(judgeData,...);endfunction Click (clickData,...) -- 点击指令  -- /// <param name="xy">坐标</param>  -- /// <param name="delayTime">延迟时间（单位：毫秒）</param>  -- /// <param name="OffsetX">手动偏移X</param>  -- /// <param name="OffsetY">手动偏移Y</param>  -- public virtual Point Click(DataClick XY, int delayTime = 0, int OffsetX = 0, int OffsetY = 0);  return C:Click(clickData,点击前延迟时间,...);endfunction Set_AddMessage (text) -- 添加一条信息到Lua信息栏  return C:Set_AddMessage(text);endfunction Overtime (time,setTime,waitTime) -- 超时  waitTime = waitTime or 200;  if time[0] > setTime then    -- 超时    C:ThrowTimeOutException();  end  Delay(waitTime);  time[0] = time[0] + waitTime;end-- C.ExecutedNumber = 0; -- 已执行的次数-- public override void StopThread();-- StopThread(); -- 结束当前脚本线程。-- luaObject.RegisterFunction("MessageBoxShow", typeof(MessageBox).GetMethod("Show", new Type[] { typeof(string) }));-- MessageBoxShow("text"); -- 弹出一个对话框并显示指定文字。--------------------------------------------------function Process ()  基本数据 ();  基本设定();  TempValue = {};  从母港进入地图();  选择出击海域 ();  战斗场次 = 0;  战斗地图到母港();  补给舰船();  if 是否开启自动入渠 then    自动入渠();  endendfunction 从母港进入地图 ()  -- 母港检测  TempValue[0] = 0;  while true do    if Judge(母港1) then      break    elseif Judge(远征回港) or Judge(远征归来1) or Judge(远征归来2) then      break    else      Overtime(TempValue,5000);    end  end  -- 母港  TempValue[0] = 0;  while true do    if Judge(母港1) then      Click(CreateDataClick("196,210|152,232|149,272|191,305|239,277|240,239|223,218","母港出击"));      break    elseif Judge(远征回港) or Judge(远征归来1) or Judge(远征归来2) then      Click(舰娘立绘);      Delay(1000);      TempValue[0] = 0;    else      Overtime(TempValue,30000);    end  end  -- 出击选择  TempValue[0] = 0;  while true do    if Judge(CreateDataJudge("140,77 @ 1EAAAB|1CB2B5|1DBDC0|1EAAAC|1EAAAC|208F90")) then -- 出击选择      Click(CreateDataClick("230,128|170,155|145,228|180,284|259,296|306,260|316,199|277,159")); -- 出击      break    else      Overtime(TempValue,30000);    end  end  -- 海域选择  TempValue[0] = 0;  while true do    if Judge(CreateDataJudge("574,122 @ FF9B42|FF9E47|FF762A|FFF6F2|FF8032","海域选择")) then      break    else      Overtime(TempValue,30000);    end  endendfunction 选择出击海域 ()  IsUsedCustomMap = 自定义海域选择();  if not IsUsedCustomMap then    -- 选择海域    TempValue[0] = 0;    local _出击海域判定数据 = {      CreateDataJudge("142,450 @ E76110|EE9570|FFFFFF|E76110"),      CreateDataJudge("232,450 @ EE9570|E76110|EA7D4A|FFFFFF"),      CreateDataJudge("308,457 @ C55F20|FFAB6F|FFDB96|C96D2A"),      CreateDataJudge("371,453 @ F46306|D36D30|FFEEE6|E3C1BB"),      CreateDataJudge("463,448 @ F59150|F16722|E95F33|F29C88"),      CreateDataJudge("525,453 @ E76110|FDF7F4|EE9570|E76110")};    local _出击海域选择数据 = {      CreateDataClick("155,424|130,436|127,456|181,456|181,446"),      CreateDataClick("234,423|206,439|205,456|260,456|260,444"),      CreateDataClick("307,427|284,438|282,455|333,457|333,443"),      CreateDataClick("377,423|353,438|351,455|403,456|402,441"),      CreateDataClick("452,422|426,435|427,456|477,457|476,435"),      CreateDataClick("527,425|501,433|501,457|556,456|557,436")};    while true do      if Judge(_出击海域判定数据[出击海域]) then        break      else        Click(_出击海域选择数据[出击海域]);        Overtime(TempValue,30000);      end    end    -- 选择地图    TempValue[0] = 0;    local _bool = true;    local _出击地图选择数据 = {      CreateDataClick("130,141|435,267"),      CreateDataClick("465,142|663,268"),      CreateDataClick("131,286|432,409"),      CreateDataClick("464,286|663,412"),      CreateDataClick("224,143|771,266"),      CreateDataClick("221,284|769,404")};    while _bool == true do      while _bool == true do        if Judge(CreateDataJudge("574,122 @ FF9B42|FF9E47|FF762A|FFF6F2|FF8032","海域选择")) then          if 出击地图 < 1 or 出击地图 > 6 then            error("选择地图有误！");          end          if 出击地图 >= 5 then            if Judge(CreateDataJudge("700,279 @ 89100D|A10D00","扩张作战")) then              Click(CreateDataClick("691,225|691,329|779,276","选择扩张作战"));              Delay(300);              break            elseif Judge(CreateDataJudge("138,276 @ EF2B2B|EC2B2B|FF2E2B","从扩张作战返回")) then            else              Overtime(TempValue,30000);              break            end          end          Click(_出击地图选择数据[出击地图]);          _bool = false;        else          Overtime(TempValue,30000);        end        break      end    end  end  -- 出击详细  TempValue[0] = 0;  while true do    if Judge(CreateDataJudge("605,76 @ 1B4C50|1DBDC0|1D2529|1DBDC0|1C7579","出击详细")) then      Click(CreateDataClick("658,435|717,451","出击决定"));      break    else      Overtime(TempValue,30000);    end  end  -- 舰队选择  TempValue[0] = 0;  while true do    if Judge(CreateDataJudge("637,130 @ 716E6D|FFF6F2|3A3A3A|E1D9D6","出击舰队选择")) then      if Judge(CreateDataJudge("644,448 @ 330F0E|441516|3E1015","出击开始可选")) or        Judge(CreateDataJudge("644,448 @ 6F0A02|7A1508|760D03","出击开始选中")) or        Judge(CreateDataJudge("612,436 @ 320807|8F6D69|AD9B92","出击开始可选(航空基地付)")) or        Judge(CreateDataJudge("612,436 @ 760B0C|BA605D|D29C99","出击开始选中(航空基地付)")) then        -- 可以出击        if 检测舰船状态(1) then        else          if 是否开启自动入渠 then            -- 检测到破损舰船！开始自动入渠            返回到母港();            自动入渠();            C:ThrowTimeOutException();-- 抛出一个超时错误，以用于再次执行脚本          else            Set_AddMessage("检测到破损舰船！将停止脚本");            返回到母港();            StopThread();-- 因大破关闭脚本          end        end        if 检测舰船状态(3) then        else -- 检测到未补给舰船！将开始补给流程。          Set_AddMessage("检测到未补给舰船！");          返回到母港();          补给舰船();          C:ThrowTimeOutException();-- 抛出一个超时错误，以用于再次执行脚本        end        if 联合舰队模式 then-- 出击时检测队伍2状态          -- 切换至队伍2          TempValue[0] = 0;          while true do            if Judge(CreateDataJudge("637,130 @ 716E6D|FFF6F2|3A3A3A|E1D9D6","出击舰队选择")) and              not Judge(CreateDataJudge("391,112 @ 23A0A1|23A0A1|77B9B9|FFF6F2","出击舰队选择队伍2")) then              Click(CreateDataClick("389,112|394,114","选择队伍2"));              Delay(200);              TempValue[0] = 0;            elseif Judge(CreateDataJudge("637,130 @ 716E6D|FFF6F2|3A3A3A|E1D9D6","出击舰队选择")) and              Judge(CreateDataJudge("391,112 @ 23A0A1|23A0A1|77B9B9|FFF6F2","出击舰队选择队伍2")) then                break            else              Overtime(TempValue,30000);            end          end          -- 检测队伍2状态          if 检测舰船状态(1) then          else -- 检测到破损舰船！将停止脚本            Set_AddMessage("检测到破损舰船！将停止脚本");            返回到母港();            StopThread();-- 因大破关闭脚本          end          if 检测舰船状态(3) then          else -- 检测到未补给舰船！将开始补给流程。            Set_AddMessage("检测到未补给舰船！");            返回到母港();            补给舰船();            C:ThrowTimeOutException();-- 抛出一个超时错误，以用于再次执行脚本          end          -- 切换至队伍1          while true do            if Judge(CreateDataJudge("637,130 @ 716E6D|FFF6F2|3A3A3A|E1D9D6","出击舰队选择")) and              not Judge(CreateDataJudge("359,111 @ 23A0A1|23A0A1|D4E1DE|FFF6F2","出击舰队选择队伍1")) then              Click(CreateDataClick("359,110|365,117","选择队伍2"));              Delay(200);              TempValue[0] = 0;            elseif Judge(CreateDataJudge("637,130 @ 716E6D|FFF6F2|3A3A3A|E1D9D6","出击舰队选择")) and              Judge(CreateDataJudge("359,111 @ 23A0A1|23A0A1|D4E1DE|FFF6F2","出击舰队选择队伍1")) then                break            else              Overtime(TempValue,30000);            end          end        end        -- 这里有坐标检测机制，必须使用偏移数据        if Judge(CreateDataJudge("612,436 @ 320807|8F6D69|AD9B92","出击开始可选(航空基地付)")) or        Judge(CreateDataJudge("612,436 @ 760B0C|BA605D|D29C99","出击开始选中(航空基地付)")) then          Click(CreateDataClick("542,427|684,442","出击开始(航空基地付)"));        else          Click(CreateDataClick("541,432|683,457","出击开始"));        end        if IsUsedCustomMap then          Set_AddMessage(string.format("已执行出击海域 自定义海域 ，第 %d 回",C.ExecutedNumber));        else          Set_AddMessage(string.format("已执行出击海域 %d-%d ，第 %d 回",出击海域,出击地图,C.ExecutedNumber));        end        break      elseif Judge(CreateDataJudge("644,448 @ 252525|303030|272727","出击开始不可选")) or      Judge(CreateDataJudge("612,436 @ 313131|565656|898989","出击开始不可选(航空基地付)")) then        -- 不可出击        Set_AddMessage("舰队不可出击，将停止脚本");        返回到母港();        StopThread();      else        Overtime(TempValue,30000);      end    else      Overtime(TempValue,30000);    end  endendfunction 战斗地图到母港 ()  while true do    战斗场次 = 战斗场次 + 1;    if IsUsedCustomMap then      Set_AddMessage(string.format("战斗地图：自定义海域 战斗场次：%d",战斗场次));    else      Set_AddMessage(string.format("战斗地图：%d-%d 战斗场次：%d",出击海域,出击地图,战斗场次));    end    -- 检测进入地图后的画面    TempValue[0] = 0;    while true do      if Judge(CreateDataJudge("398,128 @ 72706C|1B1915|191713|AFADA9","罗盘娘")) or        Judge(CreateDataJudge("758,436 @ 2C989B|BCD0D1|A1C3C5|169396","道具掉落（非舰船掉落）")) then        Click(舰娘立绘);        TempValue[0] = 0;        Delay(1000);      elseif Judge(CreateDataJudge("387,3 @ 2DA9D7|41B8D3|5DC1D0","战斗中")) or        Judge(CreateDataJudge("386,6 @ 4EABCF|247A9C|004968","夜战中")) then        TempValue[0] = 0;        Delay(1000);      elseif Judge(CreateDataJudge("52,33 @ 1DBDC0|222D30|1F7073|1DBDC0","追击判定")) or        Judge(CreateDataJudge("85,78 @ 24292C|FFF6F2|FFF6F2|FFF6F2|24292C","成果1")) or        Judge(CreateDataJudge("49,78 @ 24292C|FFF6F2|FFF6F2|FFF6F2|24292C","成果2")) then        break      end      Delay(200);      if (Judge(CreateDataJudge("442,184 @ FFF6F2|5C8F8E|D2D7D4","选择阵型")) or        Judge(CreateDataJudge("442,184 @ FFF6F2|ED9A53|F9DAC6","选择阵型(已摁下)"))) and        (Judge(CreateDataJudge("711,188 @ C6CFCC|F4EEEB|8FACAA|086D6E","选择阵型(可选轮型)")) or        Judge(CreateDataJudge("711,188 @ F7D3BA|FEEFE8|F1B485|E87E00","选择阵型(可选轮型，已摁下)"))) then          -- 可选阵型5        if 战斗阵型[战斗场次] ~= nil then          Click(出击阵型[战斗阵型[战斗场次]]);        else          Click(出击阵型[1]);-- 默认单纵阵        end        TempValue[0] = 0;        Delay(1000);      elseif (Judge(CreateDataJudge("442,184 @ FFF6F2|5C8F8E|D2D7D4","选择阵型")) or        Judge(CreateDataJudge("442,184 @ FFF6F2|ED9A53|F9DAC6","选择阵型(已摁下)"))) and        (not(Judge(CreateDataJudge("711,188 @ C6CFCC|F4EEEB|8FACAA|086D6E","选择阵型(可选轮型)")) or        Judge(CreateDataJudge("711,188 @ F7D3BA|FEEFE8|F1B485|E87E00","选择阵型(可选轮型，已摁下)")))) then        -- 可选阵型4        if 战斗阵型[战斗场次] ~= nil then          if 战斗阵型[战斗场次] == 輪形陣 then            Click(出击阵型[1]);          else            Click(出击阵型[战斗阵型[战斗场次]]);          end        else          Click(出击阵型[1]);-- 默认单纵阵        end        TempValue[0] = 0;        Delay(1000);      end      Delay(200);      if 联合舰队模式 then        if (Judge(CreateDataJudge("634,307 @ C6CFCC|FFF6F2|9EB5B3|C6CFCC","第四警戒")) or          Judge(CreateDataJudge("634,307 @ F7D3BA|FFF6F2|F3BC94|F7D3BA","第四警戒摁下"))) then          if (Judge(CreateDataJudge("500,307 @ FFF6F2|DEDFDC|C6CFCC|FFF6F2","第三警戒")) or            Judge(CreateDataJudge("500,307 @ FFF6F2|FBE1D2|F7D3BA|FFF6F2","第三警戒摁下"))) then            if 战斗阵型[战斗场次] ~= nil then              Click(联合舰队出击阵型[战斗阵型[战斗场次]]);            else              Click(联合舰队出击阵型[4]);-- 默认单纵阵            end            TempValue[0] = 0;            Delay(1000);          else            if 战斗阵型[战斗场次] ~= nil then              if 战斗阵型[战斗场次] == 第三警戒航行序列 then                Click(联合舰队出击阵型[1]);              else                Click(联合舰队出击阵型[战斗阵型[战斗场次]]);              end            else              Click(联合舰队出击阵型[4]);-- 默认单纵阵            end            TempValue[0] = 0;            Delay(1000);          end        end      end      自定义判定接口函数1();      Delay(200);      if Judge(母港1) then        return 0;      elseif Judge(远征回港) or Judge(远征归来1) or Judge(远征归来2) then        TempValue[0] = 0;        Click(舰娘立绘);      else        Overtime(TempValue,60000);      end    end    -- 战斗中    TempValue[0] = 0;    while true do      if Judge(CreateDataJudge("387,3 @ 2DA9D7|41B8D3|5DC1D0","战斗中")) or        Judge(CreateDataJudge("386,6 @ 4EABCF|247A9C|004968","夜战中")) then        TempValue[0] = 0;        Delay(1000);      elseif Judge(CreateDataJudge("52,33 @ 1DBDC0|222D30|1F7073|1DBDC0","追击判定")) then        -- 选择是否追击        if 是否追击[战斗场次] ~= nil then          Click(追击判定[是否追击[战斗场次]]);        else          Click(追击判定[1]);-- 默认不追击        end        TempValue[0] = 0;        Delay(1000);      end      Delay(200);      if Judge(CreateDataJudge("85,78 @ 24292C|FFF6F2|FFF6F2|FFF6F2|24292C","成果1")) and        Judge(CreateDataJudge("762,443 @ CDDADB","出击成果可点"),80) then        TempValue[0] = 0;        Click(舰娘立绘);      elseif Judge(CreateDataJudge("49,78 @ 24292C|FFF6F2|FFF6F2|FFF6F2|24292C","成果2")) and        Judge(CreateDataJudge("762,443 @ CDDADB","出击成果可点"),80) then        -- 战斗结束，开始检测破损        break      else        Overtime(TempValue,60000);      end    end    -- 战斗成果    TempValue[0] = 0;    while true do      if Judge(CreateDataJudge("49,78 @ 24292C|FFF6F2|FFF6F2|FFF6F2|24292C","成果2")) and        Judge(CreateDataJudge("762,443 @ CDDADB","出击成果可点"),80) then        if 检测舰船状态(2) then          BackDecision = false;        else          Set_AddMessage("检测到破损舰船！将返回母港");          BackDecision = true;        end        if 联合舰队模式 then -- 检测随伴舰队破损状态          Delay(200);          if BackDecision then -- 队伍1中有破损舰船          else            local temp = C:GetColorForPoint("274,206");-- 区别于队伍一页面的检测机制            Click(舰娘立绘);            TempValue[0] = 0;            Delay(1000);            -- 检测队伍2            while true do              if (Judge(CreateDataJudge("49,78 @ 24292C|FFF6F2|FFF6F2|FFF6F2|24292C","成果2")) and                Judge(CreateDataJudge("762,443 @ CDDADB","出击成果可点"),80)) and                (temp ~= C:GetColorForPoint("274,206")) then                  if 检测舰船状态(2) then                    BackDecision = false;                  else                    Set_AddMessage("检测到破损舰船！将返回母港");                    BackDecision = true;                  end                  break              else                Overtime(TempValue,60000);              end            end          end        end        break      else        Overtime(TempValue,30000);      end    end    -- 回港判定    TempValue[0] = 0;    while true do      if (Judge(CreateDataJudge("49,78 @ 24292C|FFF6F2|FFF6F2|FFF6F2|24292C","成果2")) and        Judge(CreateDataJudge("762,443 @ CDDADB","出击成果可点"),80)) or        Judge(CreateDataJudge("756,437 @ 36A7AA|D3E3E3|72B9BA","舰船掉落"),100) or        Judge(CreateDataJudge("673,86 @ A2514D|FFD6D3|AF7879|8C343B","旗舰大破")) or        Judge(CreateDataJudge("758,436 @ 2C989B|BCD0D1|A1C3C5|169396","道具掉落（非舰船掉落）")) or        Judge(CreateDataJudge("750,428 @ D6DDDD|A1C3C6|169396|B2CCCC|3E9DA1|D6DDDD","通关海域"),20)then          Click(舰娘立绘);          TempValue[0] = 0;          Delay(1000);      elseif Judge(CreateDataJudge("267,235 @ 718889|6A8A90|92B2BA","进击与撤退")) then        if BackDecision == false then          if 战斗场次 < table.getn(战斗阵型) then            -- 进击          else            BackDecision = true;          end        end        if BackDecision then          -- 撤退          Click(CreateDataClick("500,208|460,239|467,271|516,277|552,246|546,214","撤退"));          TempValue[0] = 0;          Delay(1000);        else          -- 进击          Click(CreateDataClick("283,203|241,238|250,273|299,279|341,247|333,211","进击"));          Delay(1000);          break        end      end      Delay(200);      if Judge(母港1) then        return 0;      elseif Judge(远征回港) or Judge(远征归来1) or Judge(远征归来2) then        TempValue[0] = 0;        Click(舰娘立绘);      else        Overtime(TempValue,30000);      end    end  endendfunction 检测舰船状态 (检测模式)  local function 在选择舰队时检测是否有破损舰船 ()    return C:DamagedReadiness({true,true,true,true,true,true},检测破损程度下限); -- 3=仅检测大破、2=检测中破和大破、1=检测小破中破和大破  end  local function 在战斗报告时检测是否有破损舰船 ()    return C:AdvancingDamagedReadiness({true,true,true,true,true,true},检测破损程度下限)-- 3=仅检测大破、2=检测中破和大破、1=检测小破中破和大破  end  local function 在选择舰队时检测是否有未补给舰船 ()    return C:SupplyReadiness(待补给舰船);  end  if 检测模式 == 1 then -- 在选择舰队时检测    return 在选择舰队时检测是否有破损舰船();  elseif 检测模式 == 2 then -- 在战斗报告时检测    return 在战斗报告时检测是否有破损舰船();  elseif 检测模式 == 3 then -- 在选择舰队时检测补给状态    return 在选择舰队时检测是否有未补给舰船();  else    Set_AddMessage("检测模式输入错误！");    StopThread();  endendfunction 返回到母港 ()  TempValue[0] = 0;  while true do    if Judge(母港1) then      break    elseif Judge(左侧返回母港1) or Judge(左侧返回母港2) then      Click(左侧返回母港);      TempValue[0] = 0;      Delay(3000);    elseif Judge(远征回港) or Judge(远征归来1) or Judge(远征归来2) then      Click(舰娘立绘);      TempValue[0] = 0;      Delay(1000);    else      Overtime(TempValue,30000);    end  endendfunction 补给舰船 ()  返回到母港();  -- 母港  TempValue[0] = 0;  while true do    if Judge(母港1) then      Click(CreateDataClick("75,189|46,218|73,248|108,221", "母港补给"));      break    elseif Judge(远征回港) or Judge(远征归来1) or Judge(远征归来2) then      Click(舰娘立绘);      Delay(1000);      TempValue[0] = 0;    else      Overtime(TempValue,30000);    end  end  -- 补给队伍1  TempValue[0] = 0;  while true do    if Judge(CreateDataJudge("646,179 @ E2E4D6|818375|797B6D|CCCEC0|EFF1E3|9A9C8E","补给页")) then      if Judge(CreateDataJudge("145,118 @ 23A0A1|23A0A1|D4E1DE|FFF6F2", "补给队伍1")) then        C:Supply(待补给舰船);        break      else        Click(CreateDataClick("145,117|152,120", "补给队伍1"));        TempValue[0] = 0;        Delay(200);      end    else      Overtime(TempValue,30000);    end  end  -- 补给队伍2  TempValue[0] = 0;  if 联合舰队模式 then    while true do      if Judge(CreateDataJudge("646,179 @ E2E4D6|818375|797B6D|CCCEC0|EFF1E3|9A9C8E","补给页")) then        if Judge(CreateDataJudge("178,119 @ 23A0A1|D4E1DE|FFF6F2|53ADAD", "补给队伍2")) then          C:Supply(待补给舰船);          break        else          Click(CreateDataClick("172,114|184,124", "补给队伍2"));          TempValue[0] = 0;          Delay(200);        end      else        Overtime(TempValue,30000);      end    end  end  返回到母港();endfunction 自动入渠 ()  返回到母港();  C:Dock(联合舰队模式,修复破损程度下限);  返回到母港();endfunction 基本数据 ()  鎮守府海域 = 1;  南西諸島海域 = 2;  北方海域 = 3;  西方海域 = 4;  南方海域 = 5;  中部海域 = 6;  単縦陣 = 1;  複縦陣 = 2;  輪形陣 = 3;  梯形陣 = 4;  単横陣 = 5;  追撃せず = 1;  夜戦突入 = 2;  母港1 = CreateDataJudge("773,449 @ E7EDEA|2EA4A7|209FA2|2EA4A7|E7EDEA","母港1");  远征回港 = CreateDataJudge("523,26 @ 36AFA7|25ACA9|18AFB1|11B0B3|17B6B9|26BEBE");  远征归来1 = CreateDataJudge("196,235 @ FFFDFC|C9D1D0|698788|698788");  远征归来2 = CreateDataJudge("22,345 @ BCBFC1|626C73|ACAFB3|C7C8CA");  左侧返回母港1 = CreateDataJudge("75,270 @ F3F4EC|ACACA7|3A3A35|B9BAB5","左侧返回母港1");  左侧返回母港2 = CreateDataJudge("75,270 @ F3F0E4|B1AEA0|3C3422|BFAF9B","左侧返回母港2");  -- xxxx = CreateDataJudge("xxx @ xxxxxx");  -- Judge(CreateDataJudge("xxx @ xxxxxx","xxxx"))  舰娘立绘 = CreateDataClick("564,181|712,376");  左侧返回母港 = CreateDataClick("60,223|86,292", "左侧返回母港");  出击阵型 = {CreateDataClick("400,173|493,195","出击单纵阵"),    CreateDataClick("530,173|623,195","出击复纵阵"),    CreateDataClick("662,173|755,195","出击轮型阵"),    CreateDataClick("468,332|561,354","出击梯形阵"),    CreateDataClick("599,332|692,354","出击单横阵")};  联合舰队出击阵型 = {CreateDataClick("446,167|548,177","第一警戒航行序列"),    CreateDataClick("622,167|702,177","第二警戒航行序列"),    CreateDataClick("453,305|540,319","第三警戒航行序列"),    CreateDataClick("628,303|706,317","第四警戒航行序列")};  追击判定 = {CreateDataClick("285,208|239,236|249,270|298,277|341,246|331,209","追撃せず"),    CreateDataClick("497,212|460,235|467,266|513,272|549,245|543,214","夜戦突入")};  -- xxxx = CreateDataClick("xxxxxx");endfunction StartMethod ()  Process();  return ("来自小轩的lua脚本...");endreturn StartMethod ();